package com.zx.back.service;import com.zx.back.mapper.OrdersMapper;import com.zx.back.model.Orders;import com.zx.back.model.User;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Service@Transactionalpublic class IordersServiceImpl implements IordersService{    @Autowired    private OrdersMapper ordersMapper;    @Override    public List<Orders> findorders(String uname) {        return ordersMapper.queryOrders(uname);    }    @Override    public void saveOrder(String date, String uid, String fid) {        ordersMapper.saveOrder(date, uid, fid);    }    @Override    public void deleteOrder(Integer oid) {        ordersMapper.deleteByPrimaryKey(oid);    }}