package com.zx.back.service;import com.zx.back.mapper.UserMapper;import com.zx.back.model.User;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;@Service@Transactionalpublic class UserServiceImpl implements IUserService {    @Autowired    private UserMapper userMapper;    @Override    public List<User> findAll() {        return userMapper.findAll();    }    @Override    public User findByName(String name) {        return userMapper.findByName(name);    }    @Override    public int savaOrUpdate(User user) {        return userMapper.insert(user);    }    @Override    public void deleteById(String id) {    }}